import React , {useState, useEffect} from 'react';

//import Conditions from '../Conditions/weatherDisplay;

// i made a folder so you can put your fetch code etc in here jamell, just to make sure we dont have files all over the place (and ensure good practice!)

     function getTheWeather() {
        
        const key = 'APP_KEY';
        const [feels_like,setFeelsLike] = useState('');
        const [mainTemp,setMainTemp] = useState('');
        const [description,setDescription] = useState('');
        const [main,setMain] = useState('');

        // your fetch code for today's weather goes here
        useEffect(()=> {
            fetch('https://api.openweathermap.org/data/2.5/weather?q=Karachi,pk&APPID=' +key+ '&units=metric')
            .then(res=>res.json())
            .then(data=>{
                console.log(data);
                setFeelsLike(data.main.feels_like);
                setMainTemp(data.main.temp);
                setDescription(data.weather[0].description);
                setMain(data.weather[0].main);
               
            })
            },[])
            return (
                <div>
                <h1>Main Temperature : {mainTemp} Degrees Celsius</h1>
                <h1>Feels like: {feels_like} Degrees Celsius</h1>
                <h1>Weather Parameter: {main}</h1>
                <h1>Description : {description}</h1>
     </div>
